// ===== DYNAMIC SNIPPET LENGTH =====
function dynamicSnippetLength() {
    var targetLength;
    var screenWidth = window.innerWidth;

    if (screenWidth <= 360) {
        targetLength = 50;
    } else if (screenWidth <= 480) {
        targetLength = 80;
    } else {
        targetLength = 200;
    }

    var snippets = document.querySelectorAll('.post-snippet');
    for (var i = 0; i < snippets.length; i++) {
        var snippet = snippets[i];
        var originalText = snippet.textContent.trim();
        if (originalText.length > targetLength) {
            var newSnippet = originalText.substring(0, targetLength) + '...';
            snippet.textContent = newSnippet;
        }
    }
}

// Initialize on load and resize
document.addEventListener('DOMContentLoaded', dynamicSnippetLength);
window.addEventListener('resize', dynamicSnippetLength);
