// ===== ERROR BOUNDARY HANDLING =====
window.addEventListener('error', function(e) {
    console.error('Global error caught:', e.error);
    var errorBoundary = document.getElementById('error-boundary');
    if (errorBoundary) errorBoundary.style.display = 'block';
});

window.addEventListener('unhandledrejection', function(e) {
    console.error('Unhandled promise rejection:', e.reason);
    var errorBoundary = document.getElementById('error-boundary');
    if (errorBoundary) errorBoundary.style.display = 'block';
});

function checkNetworkStatus() {
    if (!navigator.onLine) {
        var errorBoundary = document.getElementById('error-boundary');
        if (errorBoundary) {
            errorBoundary.style.display = 'block';
            var errorTitle = errorBoundary.querySelector('.error-content h3');
            var errorText = errorBoundary.querySelector('.error-content p');
            if (errorTitle) errorTitle.textContent = 'Koneksi Terputus';
            if (errorText) errorText.textContent = 'Periksa koneksi internet Anda dan coba lagi.';
        }
    }
}

window.addEventListener('online', function() {
    var errorBoundary = document.getElementById('error-boundary');
    if (errorBoundary) errorBoundary.style.display = 'none';
});

window.addEventListener('offline', checkNetworkStatus);
