    const menuToggle = document.querySelector('.hamburger-menu'); 
    const navbarMenu = document.getElementById('navbar-menu');

    if (menuToggle &&  navbarMenu) {
        menuToggle.addEventListener('click', function(e) {
            e.stopPropagation();

            navbarMenu.classList.toggle('active');

            // GANTI INI: dari 'is-active' menjadi 'active'
            this.classList.toggle('active'); 
        });

        document.addEventListener('click', function(e) {
            if (!navbarMenu.contains(e.target) && !menuToggle.contains(e.target)) {
                if (navbarMenu.classList.contains('active')) {
                    navbarMenu.classList.remove('active');
                    // GANTI INI juga: dari 'is-active' menjadi 'active'
                    menuToggle.classList.remove('active');
                }
            }
        });
    }
});

// ===== IN-ARTICLE ADS =====
document.addEventListener("DOMContentLoaded", function() {
  // Jalankan hanya di halaman posting
  if (!document.body.classList.contains('item-view')) return;

  const article = document.querySelector('.post-body, .entry-content');
  if (!article) return;

  // Ambil konten iklan dari widget section
  const ad1 = document.querySelector('#ads_in_article_1 .widget-content');
  const ad2 = document.querySelector('#ads_in_article_2 .widget-content');

  // Ambil semua paragraf artikel
  const paragraphs = article.querySelectorAll('p');
  if (paragraphs.length === 0) return;

  // Tentukan posisi sisipan
  const middleIndex = Math.floor(paragraphs.length / 2);
  const lastIndex = paragraphs.length - 1;

  // Sisipkan iklan pertama di tengah artikel
  if (ad1 && paragraphs[middleIndex]) {
    const clone1 = ad1.cloneNode(true);
    clone1.classList.add('ads-inserted');
    paragraphs[middleIndex].insertAdjacentElement('afterend', clone1);
  }

  // Sisipkan iklan kedua di akhir artikel
  if (ad2 && paragraphs[lastIndex]) {
    const clone2 = ad2.cloneNode(true);
    clone2.classList.add('ads-inserted');
    paragraphs[lastIndex].insertAdjacentElement('afterend', clone2);
  }
});

