document.addEventListener("DOMContentLoaded", function() {
  const toggleButton = document.getElementById('darkModeToggle');
  const body = document.body;
  const icon = document.getElementById('darkModeIcon');
  const darkModeClass = 'dark-mode';
  const storageKey = 'darkModeEnabled';

  if (!toggleButton || !body || !icon) {
    console.error('Dark Mode elements not found. Check HTML IDs.');
    return;
  }

  // Fungsi untuk mengatur mode
  function setDarkMode(isEnabled) {
    if (isEnabled) {
      body.classList.add(darkModeClass);
      icon.textContent = 'â˜€ï¸'; // Ikon Matahari (untuk mode terang)
      localStorage.setItem(storageKey, 'true');
    } else {
      body.classList.remove(darkModeClass);
      icon.textContent = 'ðŸŒ™'; // Ikon Bulan (untuk mode gelap)
      localStorage.setItem(storageKey, 'false');
    }
  }

  // 1. Cek Preferensi yang Tersimpan (LocalStorage)
  const savedPreference = localStorage.getItem(storageKey);

  if (savedPreference !== null) {
    setDarkMode(savedPreference === 'true');
  } else {
    // Cek preferensi sistem operasi
    const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
    setDarkMode(prefersDark); 
  }

  // 2. Event Listener untuk Tombol Toggle
  toggleButton.addEventListener('click', function() {
    const isCurrentlyDark = body.classList.contains(darkModeClass);
    setDarkMode(!isCurrentlyDark); // Alihkan mode
  });
});
